GraphQLSchema:
{ 
    type Query { 
      bookById (id: @id): Book 
    } 

    type Book { 
        id : ID!, 
        title : String!, 
        numberOfPages: Int, 
        author: Author
    } 

    type Author { 
        id: ID!, 
        firstName: String, 
        lastName: String, 
        address: String, 
    } 

    type Mutation { 
         addBook( 
            id : ID!, 
            title: String!, 
            numberOfPages: Int, 
            author : { 
                id: ID!, 
                firstName: String, 
                lastName: String 
                address: String 
            } 
         ): Book 
    } 
}
